const now = new Date();

function pad(n){ return String(n).padStart(2, '0'); }

const utc = new Date(now.getTime() + now.getTimezoneOffset() * 60000);
const kl  = new Date(utc.getTime() + 8 * 60 * 60 * 1000);

const tarikh = `${kl.getFullYear()}-${pad(kl.getMonth()+1)}-${pad(kl.getDate())}`;
const bulan = pad(kl.getMonth()+1);
let hour = kl.getHours();
const minute = pad(kl.getMinutes());
const second = pad(kl.getSeconds());

const ampm = hour >= 12 ? "PM" : "AM";
hour = hour % 12;
hour = hour ? hour : 12; // kalau 0 jadikan 12

const masa = `${pad(hour)}:${minute}:${second} ${ampm}`;

const weekdays = ['Ahad','Isnin','Selasa','Rabu','Khamis','Jumaat','Sabtu'];

return [
  {
    json: {
      sessionId: $input.first().json.sessionId,
      tarikh,
      bulan,
      masa,              // Format 12 jam (hh:mm:ss AM/PM)
      hari: weekdays[kl.getDay()]
    }
  }
];

