function replaceDateHijrah(dateHijrah){
  const parts = dateHijrah.split("-");
  const bulan = parts[1];
  const bulanMap = {
    "Muh": "Muharram",
    "Saf": "Safar",
    "Raw": "Rabiulawal",
    "Rak": "Rabiulakhir",
    "Jaw": "Jumadilawal",
    "Jak": "Jumadilakhir",
    "Rej": "Rejab",
    "Syb": "Syaaban",
    "Ram": "Ramadan",
    "Syw": "Syawal",
    "Zkh": "Zulkaedah",
    "Zhj": "Zulhijjah"
  };

  let bulanBaru = bulanMap[bulan] || bulan;

  return parts[0]+" "+bulanBaru+" "+parts[2]+"H";  
}

