const input = $input.first().json.output;

// Check kalau ada [[ dalam input
if (!input.includes('[[')) {
  return [{ json: { mesej: input || "" } }];
}

// Pisahkan mesej dengan data
const parts = input.split('[[');
const mesej = parts[0].trim();
let dataStr = parts[1].replace(/]]$/, '').trim();

// --- Parse belian ---
let belian = {};
const belianMatch = dataStr.match(/\[belian:\[(.*?)\]\]/);
if (belianMatch) {
  const belianStr = belianMatch[1];

  belianStr.split(';').forEach(item => {
    const [key, val] = item.split(':').map(s => s.trim());
    if (key && val) belian[key] = val;
  });
  // Buang part belian dari string utama
  dataStr = dataStr.replace(belianMatch[0], '');
}

// --- Parse field lain ---
let obj = { mesej };
dataStr.split(';').forEach(pair => {
  const [key, val] = pair.split(':').map(s => s.trim());
  if (key && val) obj[key] = val;
});

// Tambah belian
obj.belian = belian;

// Return dalam format n8n
return [{ json: obj }];

