const input = $('Simpan Tempahan').first().json['No Tel']; // contoh ambil dari field phone = "60195168839@c.us"

// ambil nombor sahaja sebelum @
let number = input.split("@")[0]; // "60195168839"

// buang kod negara "60" â†’ jadi "195168839"
if (number.startsWith("60")) {
  number = number.slice(2);
}

// pastikan mula dengan "0"
if (!number.startsWith("0")) {
  number = "0" + number;
}

// format jadi 3 digit depan + selebihnya
const formatted = number.slice(0, 3) + "-" + number.slice(3);

return [{
  json: {
    original: input,
    formatted: formatted
  }
}];

const input = $('Code2').first().json.data[0].link_gambar;

// Split string menggunakan koma sebagai pemisah
const links = input.split(",");

// trim setiap link + tambah key id (timestamp)
const picture = links.map((link, index) => {
  return {
    id: Date.now() + index,   // timestamp unik untuk setiap link
    url: link.trim()
  };
});

return [{
  json: {
    picture
  }
}];

