const items = $input.all();

let output = [];

for (const item of items) {
  if (item.json.belian) {
    for (const [key, value] of Object.entries(item.json.belian)) {
      output.push({
        json: {
          produk: key,
          kuantiti: value
        }
      });
    }
  }
}

return output;

