const input = $input.first().json.output; // ambil text asal dari AI

// Step 1: tukar \n\n jadi \n
let text = input.replace(/\n\n/g, "\n");

// Step 2: split ikut \n
let lines = text.split("\n").map(x => x.trim()).filter(x => x !== "");

// Return setiap baris jadi item berasingan
return lines.map(line => ({ json: { line } }));

let lines = $input.first().json.line;

// Kalau bukan array, tukar jadi array
if (!Array.isArray(lines)) {
  lines = [lines];
}

// Regex utk detect gambar
const imageRegex = /(https?:\/\/[^\s]+(\.jpg|\.jpeg|\.png|\.gif|\.webp)|https:\/\/drive\.google\.com\/uc\?id=[^)\s]+)/i;

return lines.map(line => {
  return {
    json: {
      text: line,
      isImage: imageRegex.test(line) // true kalau link gambar
    }
  };
});

const input = $input.first().json.text.trim();
const timestamp = Date.now(); // ambil masa sekarang dalam ms

// Regex untuk detect pattern [Title](Link)
const regex = /\[(.*?)\]\((.*?)\)/;

let output = [];

const match = input.match(regex);
if (match) {
  output = [match[1], match[2]];
} else {
  output = [input];
}

return {
  json: {
    output,
    timestamp
  }
};

